import React, { Component } from "react";
import axios from "axios";
import Navbar from "./navbar";
import Login from "./login";
import Register from "./register";
import Bestsellers from "./bestsellers";
import Mainpage from "./mainpage";
import Footer from "./footer";


class Home extends Component{
    state = {
        toggleLogin: false,
        toggleRegister: false,
        user_logged_in: false,
    }

    handleBtnCallbackLogin = () => {
        this.setState({toggleLogin: !this.state.toggleLogin, toggleRegister: false})
    }
    handleBtnCallbackRegister = () => {
        this.setState({toggleLogin: false, toggleRegister: !this.state.toggleRegister})
    }

    handleCallbackLogin = () => {
        console.log("Inne i callback Login")
        this.getSessionUser().then()
    }

    async getSessionUser() {
        console.log("Inne i callback Login")
        await axios.get('http://localhost:3001/api/users/sessUser')
            .then(response => {
                console.log("Response sessUser: ", response.data)
            }).catch(error => {
                console.log("Error sessUser: ", error.data)
            })
    }

    render() {
        return (
            <React.Fragment>
                <Mainpage />
                <Bestsellers />
                <Footer />
                <Navbar
                    loginBtnCallback = {this.handleBtnCallbackLogin}
                    registerBtnCallback = {this.handleBtnCallbackRegister}

                />
                {this.state.toggleLogin && <Login loginCallback = {this.handleCallbackLogin}/>}
                {this.state.toggleRegister && <Register/>}
            </React.Fragment>
        );
    }
}
export default Home;
