import React, {Component} from "react";
import axios from "axios";

import DangerFeedback from "./dangerFeedback";
import SuccessFeedback from "./successFeedback";

class AddProduct extends Component {
    constructor(props) {
        super(props);

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    state = {
        name: '',
        description: '',
        price: '',
        category: '',
        quantity: '',
        imageUrl: ''
    }


    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
            [name]: value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.submitUserDetails()
            .then(response => {
            }).catch(error => {
        })
    }

    async submitUserDetails() {
        const that = this;

        await axios({
            method: 'post',
            url: 'http://localhost:3001/api/products/addProduct',
            data: {
                name: this.state.name,
                description: this.state.description,
                price: this.state.price,
                category: this.state.category,
                quantity: this.state.quantity,
                imageUrl: this.state.imageUrl
            }
        }).then(function (response) {
            console.log("success")
        }).catch(function (error) {
            if (error.response) {
                console.log("Failed")
            } else if (error.request) {
                console.log("Failed")
                console.log(error.request);
            } else {
                console.log("Failed")
            }
        });

    }

    render() {
        return (

            <div className="addProduct">

            <h1 className="display-1">Velommen</h1>

                <form onSubmit={this.handleSubmit} className="form-addProduct">

                    <input type="text" onChange={this.handleInputChange}
                       className="form-control mt-2" placeholder="name" name="name" required="*" autoFocus=""/>

                   <input type="text" onChange={this.handleInputChange}
                       className="form-control mt-2" placeholder="description" name="description" required="*" autoFocus=""/>

                   <input type="text" onChange={this.handleInputChange}
                       className="form-control mt-2" placeholder="price" name="price" required="*" autoFocus=""/>

                   <input type="text" onChange={this.handleInputChange}
                       className="form-control mt-2" placeholder="category" name="category" required="*" autoFocus=""/>

                   <input type="number" onChange={this.handleInputChange}
                       className="form-control mt-2" placeholder="quantity" name="quantity" required="*" autoFocus=""/>


                    <input className="form-control mt-2" type="file" onChange={this.handleInputChange} name="imageArray" accept="image/*" required="*"
                    autoFocus="" multiple />

                    <button type="submit" className="btn btn-lg btn-primary btn-block mt-2">Add</button>
                </form>
            </div>


        );
    }
}

export default AddProduct;
