import React, {Component} from "react";
import ShoppingCartObject from "./shoppingCartObject";
import axios from "axios";

class ShoppingCart extends Component {
    state = {
        empty_cart: true,
        empty_cart_message: 'Nothing to show here.',
        cart_objects: [
            {id: 1, name: 'Brød', quantity: 2,},
            {id: 2, name: 'Bil', quantity: 2,},
            {id: 3, name: 'Bord', quantity: 2,},
            {id: 4, name: 'Blad', quantity: 2,},
        ]
    }

    async getCartObjects() {
        axios.get('http://localhost:3001/api/cart/getCart')
            .then(response => {
                if (response.data.message){
                    this.state.empty_cart = true;
                    this.state.empty_cart_message = response.data.message;
                } else {
                    this.state.empty_cart = false;
                    this.state.cart_objects = response.data
                }
            }).catch(error => {
                console.log(error.data.message)
            })
    }

    render() {
        return (
            <div className="shoppingCartContainer">
                <div className="shoppingCartHeader">
                    <div className="row">
                        <div className="col-sm-7">
                            <h4 className="text-center m-3">Shopping Cart</h4>
                        </div>
                        <div className="col-sm-5">
                            <button className="btn btn-primary float-right mt-3">Check out!</button>
                        </div>
                    </div>
                    {this.state.empty_cart && <p className="text-center mt-4"><strong>{this.state.empty_cart_message}</strong></p>}
                </div>
                {!this.state.empty_cart &&
                <div className="shoppingCartObjectsContainer">
                    {this.state.cart_objects.map(shoppingCartObject => (
                        <ShoppingCartObject
                            key={shoppingCartObject.id}
                            shoppingCartObject={shoppingCartObject}
                        />
                    ))}
                </div>}
            </div>
        );
    }
}

export default ShoppingCart;
