import React, {Component} from "react";
import axios from "axios";
import {Link} from "react-router-dom";

export default class Category extends Component{
    constructor(props) {
        super(props);
        this.state = {products: [], category: []};
    }
    componentDidMount = async () => {
        await axios({
            method: "get",
            url: 'https://localhost:3001/api/products/get/allProductsPure',
        }).then(res => {
            this.setState({category: res.data.category});
            console.log(this.state.category)

        })
    }
    handleClick(e, catName){
        const categories = this.state.categories
            .map((cat)=>{
                if (cat === catName) return {...cat, selected: true}
                else return {...cat, selected: false}
            })
        this.setState({categories})
    }
    render() {
        return(
            <div className="col-lg-3">
                <h1 className="my-4">ShopMet</h1>
                <div className="list-group">
                    {this.state.category &&
                        this.state.category.map((prod, index) => {
                            return(
                            <div key={index} className="list-group-item">{prod}
                                <button>
                                    onClick={(e) => this.handleClick(e, prod)}
                                </button>
                            </div>
                            )
                        })
                    }
                </div>
            </div>
        )
    }

}
