import React, {Component} from "react";
import axios from "axios";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        };

        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this)
    }

    handleChangeEmail(event) {
        this.setState({email: this.target.email})
        console.log("Email: "+this.state.email)
    }

    handleChangePassword(event) {
        this.setState({password: this.target.password})
        console.log("Password: "+this.state.password)
    }

    async postLoginDetails() {
        let res;
        try {
             res = await axios({
                method: 'post',
                url: 'http://localhost:3001/signIn',
                data: {
                    email: this.state.email,
                    lastName: this.state.password,
                }
            });
        } catch (e){
            console.error(e)
        }
        console.log("Emailref: " + this.state.email)
        console.log("Passwordref: " + this.state.password)
        console.log("Res: " + res)
        console.log("Res.data: " + res)
    }

    render() {
        return (
            <div className="loginContainer">
                <div id="login_div" className="row">
                    <div className="col-md-12">
                        <div className="mainLogIn">
                            <div className="form-signin">
                                <div className="text-center mb-4">
                                    <h4>Sign in to store</h4>
                                </div>
                                <div className="form-label-group">
                                    <input type="text" value={this.state.email} onChange={this.handleChangeEmail} className="form-control mt-2" placeholder="Username" name="login_username" required="" autoFocus=""/>
                                    <input type="Password" value={this.state.password} onChange={this.handleChangePassword} className="form-control mt-2" placeholder="Password" name="login_Password" required="" autoFocus=""/>
                                </div>
                                <button onClick={this.postLoginDetails} className="btn btn-lg btn-primary btn-block mt-2">Sign in
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
