import React, {Component} from "react";
import axios from "axios";
import LoadingSpinnerLargeDark from "../Spinners/LoadingSpinnerLargeDark";


export default class Mainpage extends Component {
    constructor(props) {
        super(props);
        this.state = {'product': [], 'category': "", show_categories: false, loading_product: false};
    }

    componentDidMount = async () => {
        const that = this;
        that.setState({loading_product: true})
        await axios({
            method: "get",
            url: 'https://localhost:3001/api/products/get/allProducts',
        }).then(function (response) {
            that.setState({
                product: response.data,
                loading_product: false,
            });
        }).catch(function (error) {
            if (!error.data) {
                console.log(error.data)
            }
            console.log(error);
            this.setState({loading_product: false})
        });
    };

    getCategories(category) {
        const that = this;
        that.setState({
            show_categories: true,
            loading_product: true,
        })
        axios({
            method: "get",
            url: 'https://localhost:3001/api/products/get/categories/' + category,
        }).then(function (response) {
            that.setState({
                categories: response.data,
                loading_product: false,
            });
        }).catch(function (error) {
            if (!error.data) {
                console.log(error.data)
            }
            console.log(error);
            this.setState({loading_product: false})
        });
    };


    render() {
        return (
            <div className="productContainer">
                <div className="row">
                    <div className={"col-lg-1"}>

                    </div>
                    <div className="col-lg-3">

                        <h1 className="my-4">ShopMet</h1>
                        <div className="list-group">
                            <button onClick={() => this.getCategories("Coffee Beans")}
                                    className="list-group-item">Coffee beans
                            </button>
                            <button onClick={() => this.getCategories("Coffee Capsules")}
                                    className="list-group-item">Coffee capsules
                            </button>
                            <button onClick={() => this.getCategories("Filter Ground Coffee")}
                                    className="list-group-item">Filter Ground coffee
                            </button>
                            <button onClick={() => this.getCategories("Coffee Machine")}
                                    className="list-group-item">Coffee machines
                            </button>
                            <button onClick={() => this.getCategories("Instant Coffee")}
                                    className="list-group-item">Instant Coffee
                            </button>
                            <button onClick={() => this.getCategories("Other")} className="list-group-item">Other
                            </button>
                            <button onClick={() => this.setState({show_categories: false})}
                                    className="list-group-item">All
                            </button>
                        </div>
                    </div>
                    <div className="col-lg-7">
                        <div id="carouselExampleIndicators" className="carousel slide my-4" data-ride="carousel">
                            <ol className="carousel-indicators">
                                <li data-target="#carouselExampleIndicators" data-slide-to="0" className="active"/>
                                <li data-target="#carouselExampleIndicators" data-slide-to="1"/>
                                <li data-target="#carouselExampleIndicators" data-slide-to="2"/>
                            </ol>
                            <div className="carousel-inner" role="listbox">
                                <div className="carousel-item active">
                                    <img className="d-block img-fluid"
                                         src="https://www.fino.eu/wp-content/uploads/2013/12/Coffee-Filters.jpg"
                                         alt="First slide"/>
                                </div>
                                <div className="carousel-item">
                                    <img className="d-block img-fluid"
                                         src="https://www.thegeneralpost.com/wp-content/uploads/2021/01/cofee-900x350.jpg"
                                         alt="Second slide"/>
                                </div>
                                <div className="carousel-item">
                                    <img className="d-block img-fluid"
                                         src="https://i0.wp.com/www.toptensell.com/wp-content/uploads/2020/05/best-coffee-makers.jpg?fit=900%2C350&ssl=1"
                                         alt="Third slide"/>
                                </div>
                            </div>
                            <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button"
                               data-slide="prev">
                                <span className="carousel-control-prev-icon" aria-hidden="true"/>
                                <span className="sr-only">Previous</span>
                            </a>
                            <a className="carousel-control-next" href="#carouselExampleIndicators" role="button"
                               data-slide="next">
                                <span className="carousel-control-next-icon" aria-hidden="true"/>
                                <span className="sr-only">Next</span>
                            </a>
                        </div>
                        {this.state.loading_product ? <LoadingSpinnerLargeDark/> :
                            <React.Fragment>
                                {this.state.show_categories ?
                                    <div className="row" dangerouslySetInnerHTML={{__html: this.state.categories}}/> :
                                    <div className="row" dangerouslySetInnerHTML={{__html: this.state.product}}/>
                                }
                            </React.Fragment>
                        }
                    </div>
                    <div className={"col-lg-1"}>

                    </div>
                </div>
            </div>
        )
    }
}
