import React, {Component} from "react";
import {Link} from "react-router-dom";

class Navbar extends Component {

    handleLogin = () => {
        this.props.toggleLoginCallback()
    }
    handleRegister = () => {
        this.props.toggleRegisterCallback()
    }
    handleShoppingCart = () => {
        this.props.toggleShoppingCartCallback()
    }
    handleProfile = () => {
        console.log("i navbar")
        this.props.toggleProfilePageCallback()
    }

    render() {
        return (
            <header>
                <nav className="row fixed-top align-items-center bg-dark">
                    <div className="col-8">
                        <Link to="/"> <img className="navbar-logo float-left ml-2 mt-2" src="/LogoMet.svg" alt=""/></Link>
                        <div onClick={this.handleShoppingCart}
                             className="btn text-white justify-content-center text-center">
                                        <span className="position-absolute float-right ml-4 mt-3">
                                            <span className="btn badge badge-dark border border-light">
                                                {this.props.cart_counter} - ${this.props.cart_total_price}
                                            </span>
                                        </span>
                            <button className="btn text-white pr-4">
                                <i className="material-icons">shopping_cart</i>
                            </button>
                        </div>
                    </div>
                    <div className="col-4">
                        {!this.props.isAuthenticated &&
                        <button onClick={this.handleLogin}
                                className="btn text-white float-right mr-2">Login</button>}
                        {!this.props.isAuthenticated &&
                        <button onClick={this.handleRegister}
                                className="btn text-white float-right mr-2">Register</button>}
                        {this.props.isAuthenticated &&
                        <button onClick={this.handleProfile}
                                className="btn text-white material-icons float-right text-nowrap pr-3">
                                account_box</button>}
                    </div>
                </nav>
            </header>
        );
    }
}

export default Navbar;
