import React, {Component} from "react";

class AdminRestockProduct extends Component {

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
            [name]: value
        })
    }

    render() {
        return (
            <div className="mt-4">
                <hr/>
                <div className="row justify-content-center">
                    <div className="col-md-2 text-center">
                        <p>{this.props.product.name}</p>
                    </div>
                    <div className="col-md-2 text-center">
                        <p>In Stock: {this.props.product.quantity}</p>
                    </div>
                </div>
                <div className="row justify-content-center">
                    <div className="col-md-2 text-center mb-2">
                        <input  type="number" className="form-control" placeholder="Quantity"/>
                    </div>
                    <div className="col-md-2 text-center mb-2">
                        <button onClick={() => this.props.addStock(this.props.product._id)} className="btn btn-primary">Add</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default AdminRestockProduct;
