import React, {Component} from "react";
import axios from "axios";
import {Link} from "react-router-dom";

export default class Category extends Component{
    constructor(props) {
        super(props);
        this.state = {category: ""};
    }
    async componentDidMount() {
        const that = this;
        await axios({
            method: "get",
            url: 'https://localhost:3001/api/products/get/categories/'+this.state.category,
        }).then(function (response) {
            that.setState({product: response.data});
        }).catch(function (error) {
            if (!error.data) {
                console.log(error.data)
            }
            console.log(error);
        });
    };

    render() {
        return(
            <div className="list-group">
                    <button onClick={() => this.setState({category: "Coffee beans"})} className="list-group-item">Coffee beans</button>
                    <button onClick={() => this.setState({category: "Coffee capsules"})} className="list-group-item">Coffee capsules</button>
                    <button onClick={() => this.setState({category: "Filter ground coffee"})} className="list-group-item">Filter Ground coffee</button>
                    <button onClick={() => this.setState({category: "Coffee machine"})} className="list-group-item">Coffee machines</button>
                    <button onClick={() => this.setState({category: "Other"})} className="list-group-item">Other</button>
                    <Link to="/" className="list-group-item">All</Link>
                </div>

        )
    }

}
