import React, {Component, Fragment} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import {Alert} from "react-bootstrap";

class Orders extends Component{

    constructor(props) {
        super(props);
        this.state = {user: "", orders: [], order_id: 0};
    }
    isAuthenticated = () =>
        this.props.isAuthenticated

    async getOrder(){
        const that = this;
        await axios({
            method: "get",
            url: "http://localhost:3001/api/users/get/",
        }).then(function (response){
            that.setState({
                orders: response.data.orders, order_id: response.data.orders._id,
                user: response.data});
            console.log(response.data)
        }).catch(function (error){
            console.log(error.data)
        });
    }
    render() {
        return(
            <div className="orderContainer">
            <div className="card mt-5">
                <h3 className="card-header">Previous orders</h3>
                        {this.state.orders.isEmpty() &&
                        <Alert color="info">You have no orders. Go shopping!</Alert>}
                        {!this.state.orders.isEmpty() &&
                        <Alert color="info">You are logged in as {this.state.user.firstName}. Here are your orders.</Alert>}
                    <ul className="list-group">
                        <li className="list-group-item">
                        {this.state.orders &&
                        this.state.orders.map((order, index) => {
                            return(
                                <div key={index}>
                                    {order.products.map((p, i) => {
                                        return(
                                            <div key={i}>
                                                <h6>Name: {p.name}</h6>
                                                <h6>Price: ${p.price}</h6>
                                                <h6>Date: {p.date}</h6>
                                    </div>
                                )
                            })}
                        </div>
                        )
                    })}
                </li>
                </ul>
            </div>
    </div>
        )
    }


}

export default Orders
