import React, { Component } from "react";
import Navbar from "./navbar";
import Login from "./login";
import Register from "./register";

class Home extends Component{
    state = {
        toggleLogin: false,
        toggleRegister: false,
    }

    handleCallbackLogin = (childData) => {
        this.setState({toggleLogin: !this.toggleLogin, toggleRegister: false})
    }
    handleCallbackRegister = (childData) => {
        this.setState({toggleLogin: false, toggleRegister: !this.toggleRegister})
    }

    render() {
        return (
            <React.Fragment>
                <Navbar
                    loginCallback = {this.handleCallbackLogin}
                    registerCallback = {this.handleCallbackRegister}
                />
                {this.state.toggleLogin && <Login />}
                {this.state.toggleRegister && <Register/>}

            </React.Fragment>
        );
    }
}
export default Home;
